import {
  queryProductprocess,
  createProductprocess,
  updateProductprocess,
  deleteProductprocess
} from '@/api/basic/productprocess'
import { getAllWorkshop } from '@/api/basic/workshop'
import { getAllFactory } from '@/api/basic/factory'
export default {
  name: 'productprocess',
  data() {
    return {
      // 是否显示弹出层
      open: false,
      // 表单
      form: {},
      // 表单标题
      title: '',
      // 显示搜索
      searchToggle: true,
      // 表格高度
      tableHeight: window.innerHeight,
      // 合计条数
      total: 0,
      // 遮罩层
      loading: true,
      // 查询参数
      queryParams: {
        queryText: undefined,
        pageIndex: 1,
        pageSize: 10,
        orderby: 'processNo',
        sort: 'descending'
      },
      // 工序列表
      dataProductprocess: [],
      // 表单校验S
      rules: {
        processNo: [
          { required: true, message: '工序编号不能为空', trigger: 'blur' }
        ],
        processName: [
          {
            required: true,
            message: '工序名称不能为空',
            trigger: 'blur'
          }
        ],
        workShopUID: [
          { required: true, message: '所属车间不能为空', trigger: 'blur' }
        ],
        factoryUID: [
          { required: true, message: '所属工厂不能为空', trigger: 'blur' }
        ]
      },
      // 所属车间列表
      workShopOption: [],
      // 所属工厂列表
      factoryOption: []
    }
  },
  created() {
    this.getList()
    getAllFactory(true).then(response => {
      this.factoryOption = response.data
    })
    getAllWorkshop(true).then(response => {
      this.workShopOption = response.data
    })
  },
  methods: {
    /** 查询工序列表 */
    getList() {
      this.loading = true
      queryProductprocess(this.queryParams).then(response => {
        this.dataProductprocess = response.data.dataSource
        this.total = response.data.totalCount
        this.loading = false
      })
    },
    handleQuery() {
      this.getList()
    },
    /** 重置按钮操作 */
    handleReset() {
      this.queryParams.queryText = ''
      this.getList()
    },
    /** 新增按钮操作 */
    handleCreate() {
      this.reset()
      this.open = true
      this.title = '添加工序'
    },
    /** 修改按钮操作 */
    handleUpdate(row) {
      this.reset()
      this.form = row
      this.open = true
      this.title = '修改工序'
    },
    /** 删除按钮操作 */
    handleDelete(row) {
      this.$confirm(
        '是否确认删除名称为"' + row.processName + '"的工序?',
        '警告',
        {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }
      )
        .then(() => {
          deleteProductprocess(row.id).then(response => {
            if (response.statusCode === 200) {
              this.getList()
              this.$message({
                message: '删除成功',
                type: 'success'
              })
            }
          })
        })
        .catch(function() {})
    },
    /** 提交按钮 */
    submitForm: function() {
      this.$refs['form'].validate(valid => {
        if (valid) {
          if (this.form.id !== undefined) {
            updateProductprocess(this.form).then(response => {
              if (response.statusCode === 200) {
                this.$message({
                  message: '修改成功',
                  type: 'success'
                })
                this.open = false
                this.getList()
              }
            })
          } else {
            createProductprocess(this.form).then(response => {
              if (response.statusCode === 200) {
                this.$message({
                  message: '新增成功',
                  type: 'success'
                })
                this.open = false
                this.getList()
              }
            })
          }
        }
      })
    },
    // 显示搜索
    handleSearch() {
      this.searchToggle = !this.searchToggle
    },
    // 排序操作
    handleSortable(val) {
      this.queryParams.orderby = val.prop
      this.queryParams.sort = val.order
      this.getList()
    },
    // 选择所属工厂
    handleSelectFactory() {
      this.form.workShopUID = null
    },
    // 表单重置
    reset() {
      this.form = {
        processName: undefined,
        processNo: undefined,
        createID: undefined,
        createName: undefined,
        createTime: undefined,
        enable: true,
        id: undefined,
        remark: undefined,
        updateID: undefined,
        updateName: undefined,
        updateTime: undefined,
        workShopUID: undefined,
        factoryUID: undefined
      }
    },
    // 自动计算分页 Id
    handleIndexCalc(index) {
      return (
        (this.queryParams.pageIndex - 1) * this.queryParams.pageSize + index + 1
      )
    },
    // 取消按钮
    cancel() {
      this.open = false
      this.reset()
      this.getList()
    },
    // 过滤车间
    filterWorkshop(data, search) {
      return data.filter(m => m.factoryUID === search)
    }
  }
}
