import { getLogs } from '@/api/system/logs'
import { formatDate } from '@/utils/index'
export default {
  name: 'logger',
  data() {
    return {
      // 搜索条显示
      searchToggle: true,
      // 表格高度
      tableHeight: window.innerHeight,
      // 遮罩层
      loading: true,
      // 返回结果集
      dataResult: [],
      // 合计条数
      total: 0,
      // 查询参数
      queryParams: {
        queryText: undefined,
        beginDate: formatDate(-7),
        endDate: formatDate(),
        level: undefined,
        pageIndex: 1,
        pageSize: 10,
        orderby: 'createTime',
        sort: 'descending'
      },
      levelOptions: [
        {
          key: 'TRACE',
          value: 'TRACE',
          label: 'TRACE'
        },
        {
          key: 'INFO',
          value: 'INFO',
          label: 'INFO'
        },
        {
          key: 'WARN',
          value: 'WARN',
          label: 'WARN'
        },
        {
          key: 'DEBUG',
          value: 'DEBUG',
          label: 'DEBUG'
        },
        {
          key: 'ERROR',
          value: 'ERROR',
          label: 'ERROR'
        }
      ]
    }
  },
  created() {
    this.getList()
  },
  methods: {
    // 隐藏搜索
    handleSearch() {
      this.searchToggle = !this.searchToggle
    },
    /** 查询列表 */
    getList() {
      this.loading = true
      getLogs(this.queryParams).then(response => {
        this.dataResult = response.data
        this.total = this.dataResult.totalCount
        this.loading = false
      })
    },
    // 取消按钮
    cancel() {
      this.open = false
      this.reset()
    },
    /** 重置按钮操作 */
    handleReset() {
      this.queryParams.queryText = ''
      this.getList()
    },
    /** 搜索按钮操作 */
    handleQuery() {
      this.getList()
    },
    // 自动计算分页 Id
    handleIndexCalc(index) {
      return (
        (this.queryParams.pageIndex - 1) * this.queryParams.pageSize + index + 1
      )
    },
    // 排序操作
    handleSortable(val) {
      this.queryParams.orderby = val.prop
      this.queryParams.sort = val.order
      this.getList()
    }
  }
}
