export default {
  name: 'context-menu',
  data() {
    return {
      x: null,
      y: null,
      style: {},
      show: false
    }
  },
  methods: {
    bindShowEvents(event) {
      this.style = {
        left: event.clientX + 'px',
        top: event.clientY + 'px'
      }
      this.show = true
      document.addEventListener('click', this.bindHideEvents)
    },
    // 绑定隐藏菜单事件
    bindHideEvents() {
      this.show = false
      document.removeEventListener('click', this.bindHideEvents)
    }
  }
}
